TEST(base64_test, encode_test_newline)
{
    EXPECT_EQ(Base64::encode(""s, true), "");
    EXPECT_EQ(Base64::encode("aaa"s, true), "YWFh\n");
    EXPECT_EQ(Base64::encode("aaaa"s, true), "YWFhYQ==\n");
    EXPECT_EQ(Base64::encode("aaaaa"s, true), "YWFhYWE=\n");
    EXPECT_EQ(Base64::encode("fooobarbazbaz"s, true), "Zm9vb2JhcmJhemJheg==\n");
}

