TEST(base64_decode, decode_test)
{
    EXPECT_EQ(Base64::decode(""s), "");
    EXPECT_EQ(Base64::decode("YWFh"s), "aaa");
    EXPECT_EQ(Base64::decode("YWFhYQ=="s), "aaaa");
    EXPECT_EQ(Base64::decode("YWFhYWE="s), "aaaaa"s);
    EXPECT_EQ(Base64::decode("Zm9vb2JhcmJhemJheg=="), "fooobarbazbaz"s);
    /**/
}


TEST(base64_decode, decode_test_with_whitespaces)
{
    EXPECT_EQ(Base64::decode(" \n\t    \n\n\n"s), "");
    EXPECT_EQ(Base64::decode("YW Fh\n"s), "aaa");
    EXPECT_EQ(Base64::decode("YW  FhY Q=="s), "aaaa");
    EXPECT_EQ(Base64::decode("YW FhY WE="s), "aaaaa"s);
    EXPECT_EQ(Base64::decode("Zm9vb 2Jhc m\nJhemJheg=="), "fooobarbazbaz"s);
}

